
public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int r=5;
		
		for(int i=1;i<=r;i++) {
			
			for(int j=1;j<=i;j++) {  //to print in square or Right angle Triangle pattern
			//for(int j=1;j<=r;j++) {  to print in square or rectangle pattern
				
			System.out.print("*");
			
			}	
			System.out.println();
		}
		

	}

}
